mapper.select:
select inn.id,orderId,product.id as productId,supplier.id as supplierId,product.name as productName,supplier.name as productName,productNum,createdDate,lastModifyDate,inn.info
from inn,product,supplier
where product.Id = inn.productId and inn.supplierId=supplier.id and inn.id=#{id}

mapper.update:
update Inn set orderId=#{orderId},productId=#{productId},supplierId=#{supplierId},
               productNum=#{productNum},
               lastModifyDate=#{lastModifyDate},info=#{info}
               where id=#{id}
