mapper.select:
select product.id,product.name,type.name as typeName,supplier.name as supplierName,unit,product.info,price
    from product,type,supplier where type.id = product.typeId and supplier.id=product.supplierId
    and product.name like "%"#{key}"%" order by id limit #{start},#{num}

select product.id,product.name,typeId,supplierId,unit,product.info,price
from product,type,supplier where type.id = product.typeId and supplier.id=product.supplierId
                             and product.id=#{id}
